mapper.select:
SELECT CAL_NO, CAL_CATEGORY, CAL_TITLE, CAL_CONTENT, CAL_WRITER, CAL_START, CAL_END, CAL_REGDATE, CAL_MODIFY, CAL_DELFLAG, CAL_BIGO
FROM CALENDAR
WHERE CAL_NO = #{cal_no}

mapper.update:
UPDATE CALENDAR 
SET CAL_CATEGORY=#{cal_category}, CAL_TITLE = #{cal_title}, CAL_CONTENT = #{cal_content}, CAL_START=TO_DATE(#{cal_start}, 'YYYY-MM-DD HH24:MI:SS'), CAL_END=TO_DATE(#{cal_end}, 'YYYY-MM-DD HH24:MI:SS'), CAL_MODIFY=CURRENT_DATE
WHERE CAL_NO = #{cal_no}

UPDATE CALENDAR
SET CAL_DELFLAG = 'Y', CAL_MODIFY = CURRENT_DATE 
WHERE CAL_NO = #{cal_no}
